import { useState } from 'react';
import styles from './MovieList.module.css';
import { useEffect } from 'react';

const MovieList = () => {

  const Movie = () =>{
    const [movieList , setMovieList] = useState([]);

    const getData = async() =>{
      const url = 'https://api.themoviedb.org/3/search/movie?api_key=cba95d401a14ab806ffc13a5052aab89&query=겨울왕국';
      const res = await fetch(url);
      const data = await res.json();
      console.log(data);

      setMovieList(data.results);
    }

    useEffect(()=>{
      getData();
    },[])

    return(
      <li>
      {/* <img src={movieList[1].poster_path} */}
      />
      <h2>{movieList[1].title}</h2>
      <p>평점: {movieList[1].vote_average}</p>
      <p>개봉일: {movieList[1].release_date}</p>
      <p>{movieList[1].overview}</p>
    </li>
    )
  }

  return (
    <div className={styles.movie}>
      <h1>영화 검색</h1>
      <div className={styles.search_box}>
        <input
          type="text"
          placeholder="영화 제목을 검색하세요"
        />
        <button>
          검색
        </button>
      </div>
      <ul className={styles.list}>
        <Movie></Movie>
      </ul>
    </div>
  );
};

export default MovieList;
